import CadNome from "@/pages/Modulos/Patrimonio/CadastroInstrumento/Instrumento/Cadastro/Cadastro_Nome";
import swal from "sweetalert2";
export default {
  name: "Selecao_nome",
  components: {
    CadNome: CadNome
  },
  data: function data() {
    return {
      autorizacao: false,
      nomes: [],
      nome: "",
      naipe: ""
    };
  },
  created: function created() {
    this.AtualizarNomes();
  },
  methods: {
    SelecionarCaracteristicas: function SelecionarCaracteristicas() {
      //console.log("Dados:" +this.nome);
      this.$emit("instrumento", {
        idNomeInstrumento: this.nome
      });
    },
    DeletarNome: function DeletarNome(id, nome) {
      var _this = this;

      swal.fire({
        title: "Deseja excluir o nome " + nome + "?",
        text: "Após a confirmação esta ação não poderá ser revertida!",
        type: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Sim, deletar"
      }).then(function (result) {
        if (result.value) {
          _this.Deletar(id);

          swal.fire("Deletado!", "O nome " + nome + " foi deletado com sucesso.", "success");
        }
      });
    },
    Deletar: function Deletar(id) {
      var _this2 = this;

      //endpoit que fará a deleção do item nome
      var usuarioAutorize = sessionStorage.getItem("usuario");

      if (!usuarioAutorize) {
        this.$router.push("/");
        var toast = swal.mixin({
          toast: true,
          position: "top-end",
          showConfirmButton: false,
          timer: 3000
        });
        toast({
          type: "error",
          title: "Sem autorização, entre com seus dados novamente!"
        });
      } else if (usuarioAutorize) {
        this.autorizacao = JSON.parse(usuarioAutorize);
        this.$http.delete(this.$url + "NomeInstrumento/v1/" + id, {
          headers: {
            authorization: "Bearer " + this.autorizacao.accessToken
          }
        }).then(function (response) {
          //console.log(response.status);
          //console.log("deletando nome");
          if (response.status == 204) {
            //console.log("dados do nome");
            //console.log(response.data);
            _this2.AtualizarNomes(); //Criar um loop pois o dado "data" que é recebido é um array

          } else if (response.status == 401) {
            _this2.$router.push("/");

            var _toast = swal.mixin({
              toast: true,
              position: "top-end",
              showConfirmButton: false,
              timer: 3000
            });
          }
        }).catch(function (e) {
          swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

          _this2.$router.push("/"); //console.error(e);
          //console.data(e.data);


          if (e.data.status == 401) {
            _this2.$router.push("/");
          }
        });
      }
    },
    AtualizarNomes: function AtualizarNomes() {
      var _this3 = this;

      this.nomes = [];
      var usuarioAutorize = sessionStorage.getItem("usuario");
      this.autorizacao = JSON.parse(usuarioAutorize);
      this.$http.get(this.$url + "NomeInstrumento/v1", {
        headers: {
          authorization: "Bearer " + this.autorizacao.accessToken
        }
      }).then(function (response) {
        //console.log(response.status);
        if (response.status == 200) {
          //console.log("request ok! ", new Date().toISOString().substr(0, 16));
          _this3.nomes = response.data;
        } else if (response.status == 401) {
          _this3.$router.push("/");
        }
      }).catch(function (e) {
        swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

        _this3.$router.push("/"); //console.error(e);
        //console.data(e.data);

      });
    }
  }
};
