import CadMarca from "@/pages/Modulos/Patrimonio/CadastroInstrumento/Instrumento/Cadastro/Cadastro_Marca";
import swal from "sweetalert2";
export default {
  name: "Selecao_Marca",
  components: {
    CadMarca: CadMarca
  },
  data: function data() {
    return {
      autorizacao: false,
      marcas: [],
      marca: ""
    };
  },
  created: function created() {
    this.AtualizarMarcas();
  },
  methods: {
    Selecionar: function Selecionar(marca) {
      /*console.log(
        "modificar marca " + marca + " de acordo com o instrumento selecionado!"
      );*/
      this.$emit("marca_instrumento", {
        marca: this.marca
      });
    },
    DeletarMarca: function DeletarMarca(marca) {
      var _this = this;

      swal.fire({
        title: "Deseja excluir a marca " + marca + "?",
        text: "Após a confirmação esta ação não poderá ser revertida!",
        type: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Sim, deletar"
      }).then(function (result) {
        if (result.value) {
          _this.Deletar(marca);

          swal.fire("Deletado!", "A Marca " + marca + " foi deletado com sucesso.", "success");
        }
      });
    },
    Deletar: function Deletar(marca) {
      var _this2 = this;

      //endpoit que fará a deleção do item nome
      var usuarioAutorize = sessionStorage.getItem("usuario");

      if (!usuarioAutorize) {
        this.$router.push("/");
        var toast = swal.mixin({
          toast: true,
          position: "top-end",
          showConfirmButton: false,
          timer: 3000
        });
        toast({
          type: "error",
          title: "Sem autorização, entre com seus dados novamente!"
        });
      } else if (usuarioAutorize) {
        this.autorizacao = JSON.parse(usuarioAutorize);
        this.$http.delete(this.$url + "MarcaInstrumento/v1/deletar-por-marca?marca=" + marca, {
          headers: {
            authorization: "Bearer " + this.autorizacao.accessToken
          }
        }).then(function (response) {
          //console.log(response.status);
          //console.log("deletando nome");
          if (response.status == 204) {
            //console.log("dados da marca");
            //console.log(response.data);
            _this2.AtualizarMarcas(); //Criar um loop pois o dado "data" que é recebido é um array

          } else if (response.status == 401) {
            _this2.$router.push("/");

            var _toast = swal.mixin({
              toast: true,
              position: "top-end",
              showConfirmButton: false,
              timer: 3000
            });
          }
        }).catch(function (e) {
          swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

          _this2.$router.push("/"); //console.error(e);
          //console.data(e.data);

        });
      }
    },
    AtualizarMarcas: function AtualizarMarcas() {
      var _this3 = this;

      this.marcas = [];
      var usuarioAutorize = sessionStorage.getItem("usuario");
      this.autorizacao = JSON.parse(usuarioAutorize);
      this.$http.get(this.$url + "MarcaInstrumento/v1", {
        headers: {
          authorization: "Bearer " + this.autorizacao.accessToken
        }
      }).then(function (response) {
        //console.log(response.status);
        if (response.status == 200) {
          //console.log("request ok! ", new Date().toISOString().substr(0, 16));
          response.data.forEach(function (element) {
            //console.log("Pós cadastro", element.marca);
            _this3.marcas.push(element.marca);
          });
        } else if (response.status == 401) {}
      }).catch(function (e) {
        swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

        _this3.$router.push("/"); //console.error(e);
        //console.data(e.data);

      });
    }
  }
};
