import CadComponente from "@/pages/Modulos/Patrimonio/CadastroInstrumento/Instrumento/Cadastro/Cadastro_Componente";
import swal from "sweetalert2";
export default {
  name: "Selecao_Componente",
  components: {
    CadComponente: CadComponente
  },
  data: function data() {
    return {
      autorizacao: false,
      componentes: [],
      ano: "",
      selectComponentes: []
    };
  },
  created: function created() {
    this.AtualizarComponentes();
  },
  methods: {
    Selecionar: function Selecionar(componentes) {
      this.$emit("componentes_instrumento", {
        componentes: this.selectComponentes
      });
    },
    DeletarComponente: function DeletarComponente(id, componente) {
      var _this = this;

      swal.fire({
        title: "Deseja excluir o componente " + componente + "?",
        text: "Após a confirmação esta ação não poderá ser revertida!",
        type: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Sim, deletar"
      }).then(function (result) {
        if (result.value) {
          _this.Deletar(id);

          swal.fire("Deletado!", "O componente " + componente + " foi deletado com sucesso.", "success");
        }
      });
    },
    Deletar: function Deletar(id) {
      var _this2 = this;

      //endpoit que fará a deleção do item ano
      var usuarioAutorize = sessionStorage.getItem("usuario");

      if (!usuarioAutorize) {
        this.$router.push("/");
        var toast = swal.mixin({
          toast: true,
          position: "top-end",
          showConfirmButton: false,
          timer: 3000
        });
        toast({
          type: "error",
          title: "Sem autorização, entre com seus dados novamente!"
        });
      } else if (usuarioAutorize) {
        this.autorizacao = JSON.parse(usuarioAutorize);
        this.$http.delete(this.$url + "ComponenteInstrumento/v1/" + id, {
          headers: {
            authorization: "Bearer " + this.autorizacao.accessToken
          }
        }).then(function (response) {
          //console.log(response.status);
          //console.log("deletando componente");
          if (response.status == 204) {
            //console.log("dados do componente");
            //console.log(response.data);
            _this2.AtualizarComponentes(); //Criar um loop pois o dado "data" que é recebido é um array

          } else if (response.status == 401) {
            _this2.$router.push("/");

            var _toast = swal.mixin({
              toast: true,
              position: "top-end",
              showConfirmButton: false,
              timer: 3000
            });
          }
        }).catch(function (e) {
          swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

          _this2.$router.push("/"); //console.error(e);
          //console.data(e.data);

        });
      }
    },
    AtualizarComponentes: function AtualizarComponentes() {
      var _this3 = this;

      this.componentes = [];
      var usuarioAutorize = sessionStorage.getItem("usuario");
      this.autorizacao = JSON.parse(usuarioAutorize);
      this.$http.get(this.$url + "ComponenteInstrumento/v1", {
        headers: {
          authorization: "Bearer " + this.autorizacao.accessToken
        }
      }).then(function (response) {
        //console.log(response.status);
        if (response.status == 200) {
          //console.log("request ok! ", new Date().toISOString().substr(0, 16));
          _this3.componentes = response.data;
        } else if (response.status == 401) {}
      }).catch(function (e) {
        swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

        _this3.$router.push("/"); //console.error(e);
        //console.data(e.data);

      });
    }
  }
};
