import CadOrigem from "@/pages/Modulos/Patrimonio/CadastroInstrumento/Doacao/Origem/CadastroOrigem";
import swal from "sweetalert2";
export default {
  name: "SelecaoOrigem",
  components: {
    CadOrigem: CadOrigem
  },
  data: function data() {
    return {
      isUpdating: false,
      autorizacao: false,
      origens: [],
      origem: null
    };
  },
  created: function created() {
    this.AtualizarOrigens();
  },
  watch: {
    isUpdating: function isUpdating(val) {
      var _this = this;

      if (val) {
        setTimeout(function () {
          return _this.isUpdating = false;
        }, 3000);
      }
    }
  },
  methods: {
    remove: function remove(item) {
      var index = this.origens.indexOf(item.name);
      if (index >= 0) this.origens.splice(index, 1);
    },
    Selecionar: function Selecionar(doador, cidade, estado) {
      //console.log(doador, cidade, estado)
      this.$emit("origem_instrumento", {
        origem: doador + ", " + cidade + "/" + estado
      });
    },
    DeletarOrigem: function DeletarOrigem(id, origem) {
      var _this2 = this;

      swal.fire({
        title: "Deseja excluir a origem " + origem.doador + " " + origem.cidade + " " + origem.estado + "?",
        text: "Após a confirmação esta ação não poderá ser revertida!",
        type: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Sim, deletar"
      }).then(function (result) {
        if (result.value) {
          _this2.Deletar(id);

          swal.fire("Deletado!", "A origem " + origem.doador + " " + origem.cidade + " " + origem.estado + " foi deletado com sucesso.", "success");
        }
      });
    },
    Deletar: function Deletar(id) {
      var _this3 = this;

      //endpoit que fará a deleção do item ano
      var usuarioAutorize = sessionStorage.getItem("usuario");

      if (!usuarioAutorize) {
        this.$router.push("/");
        var toast = swal.mixin({
          toast: true,
          position: "top-end",
          showConfirmButton: false,
          timer: 3000
        });
        toast({
          type: "error",
          title: "Sem autorização, entre com seus dados novamente!"
        });
      } else if (usuarioAutorize) {
        this.autorizacao = JSON.parse(usuarioAutorize);
        this.$http.delete(this.$url + "OrigemInstrumento/v1/" + id, {
          headers: {
            authorization: "Bearer " + this.autorizacao.accessToken
          }
        }).then(function (response) {
          //console.log(response.status);
          //console.log("deletando origem");
          if (response.status == 204) {
            //console.log("dados do origem");
            //console.log(response.data);
            _this3.AtualizarOrigens(); //Criar um loop pois o dado "data" que é recebido é um array

          } else if (response.status == 401) {
            _this3.$router.push("/");

            var _toast = swal.mixin({
              toast: true,
              position: "top-end",
              showConfirmButton: false,
              timer: 3000
            });
          }
        }).catch(function (e) {
          swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

          _this3.$router.push("/"); //console.error(e);
          //console.data(e.data);

        });
      }
    },
    AtualizarOrigens: function AtualizarOrigens() {
      var _this4 = this;

      this.origens = [];
      var usuarioAutorize = sessionStorage.getItem("usuario");
      this.autorizacao = JSON.parse(usuarioAutorize);
      this.$http.get(this.$url + "OrigemInstrumento/v1", {
        headers: {
          authorization: "Bearer " + this.autorizacao.accessToken
        }
      }).then(function (response) {
        //console.log(response.status);
        if (response.status == 200) {
          //console.log(
          //  "ORIGENS DATA ok! ",
          //  new Date().toISOString().substr(0, 16)
          //);
          _this4.origens = response.data; //console.log(response.data);
          //console.log(this.origens);
        } else if (response.status == 401) {}
      }).catch(function (e) {
        swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

        _this4.$router.push("/"); //console.error(e);
        //console.data(e.data);

      });
    }
  }
};
