import CadAno from "@/pages/Modulos/Patrimonio/CadastroInstrumento/Instrumento/Cadastro/Cadastro_Ano";
import swal from "sweetalert2";
export default {
  name: "Selecao_Ano",
  components: {
    CadAno: CadAno
  },
  data: function data() {
    return {
      autorizacao: false,
      anos: [],
      ano: ""
    };
  },
  created: function created() {
    this.AtualizarAnos();
  },
  methods: {
    Selecionar: function Selecionar(ano) {
      this.$emit("ano_instrumento", {
        ano: this.ano
      });
    },
    DeletarAno: function DeletarAno(ano) {
      var _this = this;

      swal.fire({
        title: "Deseja excluir o ano " + ano + "?",
        text: "Após a confirmação esta ação não poderá ser revertida!",
        type: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Sim, deletar"
      }).then(function (result) {
        if (result.value) {
          _this.Deletar(ano);

          swal.fire("Deletado!", "O ano " + ano + " foi deletado com sucesso.", "success");
        }
      });
    },
    Deletar: function Deletar(ano) {
      var _this2 = this;

      //endpoit que fará a deleção do item ano
      var usuarioAutorize = sessionStorage.getItem("usuario");

      if (!usuarioAutorize) {
        this.$router.push("/");
        var toast = swal.mixin({
          toast: true,
          position: "top-end",
          showConfirmButton: false,
          timer: 3000
        });
        toast({
          type: "error",
          title: "Sem autorização, entre com seus dados novamente!"
        });
      } else if (usuarioAutorize) {
        this.autorizacao = JSON.parse(usuarioAutorize);
        this.$http.delete(this.$url + "Ano/v1/deletar-por-ano?ano=" + ano, {
          headers: {
            authorization: "Bearer " + this.autorizacao.accessToken
          }
        }).then(function (response) {
          //console.log(response.status);
          //console.log("deletando ano");
          if (response.status == 204) {
            //console.log("dados do ano");
            //console.log(response.data);
            _this2.AtualizarAnos(); //Criar um loop pois o dado "data" que é recebido é um array

          } else if (response.status == 401) {
            _this2.$router.push("/");

            var _toast = swal.mixin({
              toast: true,
              position: "top-end",
              showConfirmButton: false,
              timer: 3000
            });
          }
        }).catch(function (e) {
          swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

          _this2.$router.push("/"); //console.error(e);
          //console.data(e.data);

        });
      }
    },
    AtualizarAnos: function AtualizarAnos() {
      var _this3 = this;

      this.anos = [];
      var usuarioAutorize = sessionStorage.getItem("usuario");
      this.autorizacao = JSON.parse(usuarioAutorize);
      this.$http.get(this.$url + "Ano/v1", {
        headers: {
          authorization: "Bearer " + this.autorizacao.accessToken
        }
      }).then(function (response) {
        //console.log(response.status);
        if (response.status == 200) {
          //console.log("request ok! ", new Date().toISOString().substr(0, 16));
          response.data.forEach(function (element) {
            //console.log("Pós busca - ano: ", element.numeroAno);
            _this3.anos.push(element.numeroAno);

            if (element.numeroAno > 0) {
              //console.log("Numero ", element.numeroAno);
              _this3.anos.sort();

              _this3.anos.reverse();
            } else {//console.log("Texto", element.numeroAno);
            }
          });
        } else if (response.status == 401) {}
      }).catch(function (e) {
        swal.fire("Sua autorização expirou!", "Entre com suas credencias novamente.", "warning");

        _this3.$router.push("/"); //console.error(e);
        //console.data(e.data);

      });
    }
  }
};
